const colorThemes=document.querySelectorAll('[name="theme"]'),applyTheme=()=>{const o=localStorage.getItem("theme");colorThemes.forEach(e=>{e.id===o&&(e.checked=!0)})},storeTheme=e=>{localStorage.setItem("theme",e)};colorThemes.forEach(e=>{e.addEventListener("click",()=>{storeTheme(e.id)})});export default applyTheme();